// Scene names
var scenes = [
    "Heating",
    "Cooling"
]

var indoorTempThreshold = 30;
var outdoorTempThreshold = 20;

setState('0_userdata.0.variables.fan_power', getState('mqtt.0.homie.fan-controller.fan.power').val);
setState('0_userdata.0.variables.clima_setpoint', getState('mqtt.0.homie.clima-controller.PID.setpoint').val);
setState('0_userdata.0.variables.clima_pid_enabled', Boolean(getState('mqtt.0.homie.clima-controller.PID.enabled').val));

on('0_userdata.0.variables.fan_power', function(dp) {
   log("Change Fan Power to: " + dp.state.val);
   setState('mqtt.0.homie.fan-controller.fan.power.set', Number(dp.state.val));
});

on('0_userdata.0.variables.clima_setpoint', function(dp) {
   log("Change PID Setpoint to: " + dp.state.val);
   setState('mqtt.0.homie.clima-controller.PID.setpoint.set', Number(dp.state.val));
});

on('0_userdata.0.variables.clima_pid_enabled', function(dp) {
   log("Change PID enabled to: " + dp.state.val);
   setState('mqtt.0.homie.clima-controller.PID.enabled.set', Boolean(dp.state.val));
});

on('0_userdata.0.variables.active_clima_scene', function(dp) {
   log("Change Clima scene to: " + dp.state.val);
    deactivateAllScenes();
    activateScene(dp.state.val);
});

// Use indoor temperature to check clima scene conditions
on('mqtt.0.homie.clima-sensor.indoor-sensor.temperature', function(dp) {

    if(Number(dp.state.val) > indoorTempThreshold) {
       if(getActiveScene() == 'Heating') {
            setState('0_userdata.0.variables.active_clima_scene', 'Cooling');
       }
    } else {
       if(getActiveScene() == 'Cooling') {
            setState('0_userdata.0.variables.active_clima_scene', 'Heating');
       }
    }

});

function activateScene(scene) {
    setState('scene.0.Clima.'+scene, true);
}

function deactivateScene(scene) {
    setState('scene.0.Clima.'+scene, false);
}

function getActiveScene() {
    for (var scene of scenes) {
        if( getState('scene.0.Clima.' + scene).val == true ) {
            return scene;
        }
    } 
    return "";
}

function deactivateAllScenes() {
    for (var scene of scenes) {
        if( getState('scene.0.Clima.' + scene).val == true ) {
            setState('scene.0.Clima.' + scene, false);
        }
    } 
}


// Chanage fan power randomly to simulate the nature
//schedule('*/04 * * * *', randomFanPower); 

function randomFanPower() {
    // only at day time
    if(getState('mqtt.0.homie.light-controller.led-modules.flux.set').val == true) {
        setState('0_userdata.0.variables.fan_power', Number(randomIntFromInterval(2,100)));
    }  
}

function randomIntFromInterval(min, max) { // min and max included 
  return Math.floor(Math.random() * (max - min + 1) + min)
}

