var qStepsPerRev = 800;
var hStepsPerRev = 400;
var gain = 45/23;

var qStepsPerMilli = qStepsPerRev * gain / 3;
var hStepsPerMilli = hStepsPerRev * gain / 3;

var totalHeight = 1340;
var driveHeight = 140;
var camDriveToolOffset = 27;
var lightDriveToolOffset = -250;
var lightDriveZeroY = 1330;

var plantLightOffsett = 150;


function moveLightDriveToPostion(pos) {
    if(getState('mqtt.0.homie.light-drive.stepper.home').val == 1) {
        return false;
    }
    var moveTo = (lightDriveZeroY * hStepsPerMilli) - (pos * hStepsPerMilli);
    setState('mqtt.0.homie.light-drive.stepper.position.set', Math.floor(moveTo));
}

function moveLightToPostion(pos) {
    if(getState('mqtt.0.homie.light-drive.stepper.home').val == 1) {
        return false;
    }
    var moveTo = (lightDriveZeroY * hStepsPerMilli) - (pos * hStepsPerMilli) + (lightDriveToolOffset * hStepsPerMilli);
    setState('mqtt.0.homie.light-drive.stepper.position.set', Math.floor(moveTo));
}

function moveCameraDriveToPostion(pos) {
    if(getState('mqtt.0.homie.camera-drive.stepper.home').val == 1) {
        return false;
    }
    var moveTo = (pos * qStepsPerMilli);
    setState('mqtt.0.homie.camera-drive.stepper.position.set', Math.floor(moveTo));
}

function moveCameraToPostion(pos) {
    if(getState('mqtt.0.homie.camera-drive.stepper.home').val == 1) {
        return false;
    }
    var moveTo = (pos * qStepsPerMilli) - (camDriveToolOffset * qStepsPerMilli);
    setState('mqtt.0.homie.camera-drive.stepper.position.set', Math.floor(moveTo));
}

on('mqtt.0.homie.light-drive.stepper.position', function(dp) {
    var posInMM = lightDriveZeroY - dp.state.val / hStepsPerMilli;
    setState('0_userdata.0.variables.drive_light_current_position', Math.round(posInMM));
    setState('0_userdata.0.variables.drive_light_tool_position', Math.round(posInMM) + lightDriveToolOffset);
});

on('mqtt.0.homie.camera-drive.stepper.position', function(dp) {
    var posInMM = dp.state.val / qStepsPerMilli;
    setState('0_userdata.0.variables.drive_cam_current_position', Math.round(posInMM));
    setState('0_userdata.0.variables.drive_cam_tool_position', Math.round(posInMM) + camDriveToolOffset);
});


var measurementLoops = 0;
var measurementLoopsMax = 5;
var measurementErrorCount = 0;

// Measure plant height every x minutes
//schedule('*/30 * * * *', prepareMeasurement); 

function prepareMeasurement() {
    // only measure if light is on
    if(getState('mqtt.0.homie.light-controller.led-modules.flux.set').val == true) {
        measurementLoops = 0;
        measurementErrorCount = 0;
        measure();
    } else {

    }
}

function updateHeight(height) {
    setState('0_userdata.0.variables.plant_height_measured', height);
}

function getCameraDrivePostion() {
    return getState('0_userdata.0.variables.drive_cam_current_position').val;
}

function getCameraToolPostion() {
    return getState('0_userdata.0.variables.drive_cam_tool_position').val;
}

function getLightDrivePostion() {
    return getState('0_userdata.0.variables.drive_light_current_position').val;
}

function getLightToolPostion() {
    return getState('0_userdata.0.variables.drive_light_tool_position').val;
}

function saveImageAsState(imageAsBase64) {
    createState('0_userdata.0.variables.pic_cam_right_measurement', {type: 'file', name: 'Picture'});
    var fs = require('fs');
    imageAsBase64 = imageAsBase64.substring(2);
    imageAsBase64 = imageAsBase64.substring(0, imageAsBase64.length - 1);
    var buf = new Buffer(imageAsBase64, 'base64');
    setBinaryState('0_userdata.0.variables.pic_cam_right_measurement', buf);
    log('save blob');
}

function measure() {
    log("measure!");
    var url = 'http://192.168.50.31:5000/cam/right/measure?drivePosition='+getCameraDrivePostion();
    var request = require('request');
    var options = {url: url, method: 'GET', headers: { 'User-Agent': 'request' }};
    request(options, function(error, response, body) {

        var delta = 0;

        console.log('service response:' + response.statusCode)

        if (!error && response.statusCode == 200) {

            var result = JSON.parse(body);
            var deltaYinMM = result.deltaYinMM; 
            var deltaYinPX = result.deltaYinPX; 
            var currentPosition = result.currentPosition;

            // Save image as html for later use in jarvis
            saveImageAsState(result.image);

            console.log("deltaYinMM " + deltaYinMM);
            console.log("deltaYinPX " + deltaYinPX);
            console.log("currentPosition " + currentPosition);



            // Stop measurment if delta is near to the top line
            if(Math.abs(deltaYinPX) < 5) {
                console.log('Measurement completed with height: ' + currentPosition);
                updateHeight(currentPosition);

                // automatically change light position
                // Offset between light and plant should be dynamic in further releases
                console.log('Move light to position: ' + (currentPosition + plantLightOffsett).toString());
                moveLightToPostion(currentPosition + plantLightOffsett);

                // Lift the camera up in order to see more on the left eye
                moveCameraDriveToPostion(getCameraDrivePostion() + 60);

                return;
            }

            var targetPosition = null;

            // If delta Y is positiv
            // Detected top line is below horizontal image center
            // Camera needs to move up 
            if(deltaYinPX > 0 && Math.abs(deltaYinPX) >= 5) {

                delta = Math.floor(deltaYinPX / 5);
                targetPosition = delta + getCameraDrivePostion();
                console.log("new delta: " + delta + ' current position: ' + getCameraDrivePostion() + '  target position: ' + targetPosition);
                moveCameraDriveToPostion(targetPosition);
                measurementLoops++;

            }


            // If delta Y is negativ
            // Detected top line is above horizontal image center
            // Camera needs to move down 
            if(deltaYinPX < 0 && Math.abs(deltaYinPX) >= 5) {

                delta = Math.floor(deltaYinPX / 5);
                targetPosition = getCameraDrivePostion() + delta;
                console.log("new delta: " + delta + ' current position: ' + getCameraDrivePostion() + '  target position: ' + targetPosition);
                moveCameraDriveToPostion(targetPosition);
                measurementLoops++;
            }

            if(measurementLoops < measurementLoopsMax) {
                setTimeout(function() {
                    measure();
                }, 10000);
            } else {
                console.log('Could not complete mesasurement in ' + measurementLoopsMax + ' loops');
            }


        } else {
           measurementErrorCount++;
           if(measurementErrorCount < 5) {
             measure(); 
           } else {
             console.log('Could not complete mesasurement!')
           }
        }
    });
}

