    var options = { url: url, method: 'GET', headers: { 'User-Agent': 'request' } };
    request(options, function (error, response, body) {
        var delta = 0;
        console.log('service response:' + response.statusCode);
        if (!error && response.statusCode == 200) {
            var result = JSON.parse(body);
            var deltaYinMM = result.deltaYinMM;
            var deltaYinPX = result.deltaYinPX;
            var currentPosition = result.currentPosition;
            // Save image as html for later use in jarvis
            saveImageAsState(result.image);
            console.log("deltaYinMM " + deltaYinMM);
            console.log("deltaYinPX " + deltaYinPX);
            console.log("currentPosition " + currentPosition);
            // Stop measurment if delta is near to the top line
            if (Math.abs(deltaYinPX) < 5) {
                console.log('Measurement completed with height: ' + currentPosition);
                updateHeight(currentPosition);
                // automatically change light position
                // Offset between light and plant should be dynamic in further releases
                console.log('Move light to position: ' + (currentPosition + plantLightOffsett).toString());
                moveLightToPostion(currentPosition + plantLightOffsett);
                // Lift the camera up in order to see more on the left eye
                moveCameraDriveToPostion(getCameraDrivePostion() + 60);
                return;
            }
            var targetPosition = null;
            // If delta Y is positiv
            // Detected top line is below horizontal image center
            // Camera needs to move up 
            if (deltaYinPX > 0 && Math.abs(deltaYinPX) >= 5) {
                delta = Math.floor(deltaYinPX / 5);
                targetPosition = delta + getCameraDrivePostion();
                console.log("new delta: " + delta + ' current position: ' + getCameraDrivePostion() + '  target position: ' + targetPosition);
                moveCameraDriveToPostion(targetPosition);
                measurementLoops++;
            }
            // If delta Y is negativ
            // Detected top line is above horizontal image center
            // Camera needs to move down 
            if (deltaYinPX < 0 && Math.abs(deltaYinPX) >= 5) {
                delta = Math.floor(deltaYinPX / 5);
                targetPosition = getCameraDrivePostion() + delta;
                console.log("new delta: " + delta + ' current position: ' + getCameraDrivePostion() + '  target position: ' + targetPosition);
                moveCameraDriveToPostion(targetPosition);
                measurementLoops++;
            }
            if (measurementLoops < measurementLoopsMax) {
                setTimeout(function () {
                    measure();
                }, 10000);
            }
            else {
                console.log('Could not complete mesasurement in ' + measurementLoopsMax + ' loops');
            }
        }
        else {
            measurementErrorCount++;
            if (measurementErrorCount < 5) {
                measure();
            }
            else {
                console.log('Could not complete mesasurement!');
            }
        }
    });
