// Scene names
var scenes = [
    "None",
    "Germination",
    "Growth",
    "Flowering"
]

var timerDayStart;
var timerDayEnd;

// Clear states after startup
deactivateAllScenes();

// Check if scene is allready selected (not None)
if(getState('0_userdata.0.variables.active_growthphase_scene').val != undefined && getState('0_userdata.0.variables.active_growthphase_scene').val != "None") {
    prepareScene(getState('0_userdata.0.variables.active_growthphase_scene').val );
}


function deactivateAllScenes() {
    for (var scene of scenes) {
        if( getState('scene.0.GrowthPhase.' + scene).val == true ) {
            setState('scene.0.GrowthPhase.' + scene, false);
        }
    } 
}

function getActiveScene() {
    for (var scene of scenes) {
        if( getState('scene.0.GrowthPhase.' + scene).val == true ) {
            return scene;
        }
    } 
    return "";
}

function getMemberById(scene, memberId) {
    var members = getObject('scene.0.GrowthPhase.' + scene).native.members;

    for (var i = 0; i < members.length; i++){
       if(members[i].id == memberId){
           return members[i];
       }
    }

    return null;
}


function prepareScene(scene) {

    var shouldStartAt = getMemberById(scene, '0_userdata.0.variables.day_start').setIfTrue;
    var shouldEndAt = getMemberById(scene, '0_userdata.0.variables.day_end').setIfTrue;

    // check if the scene should be activated right now
    console.log("Scene start/end time: " + shouldStartAt + " / " + shouldEndAt);

    // clear former schedules
    clearSchedule(timerDayStart);
    clearSchedule(timerDayEnd);

    // Activate schedules
    timerDayStart = schedule(shouldStartAt.split(':')[1] + ' ' + shouldStartAt.split(':')[0] + ' * * *', function() {
        activateScene(scene);
    });

    timerDayEnd = schedule(shouldEndAt.split(':')[1] + ' ' + shouldEndAt.split(':')[0] + ' * * *', function() {
        deactivateScene(scene);
    });

    if(compareTime(shouldStartAt, shouldEndAt, 'between') == true) {
        console.log("Scene Should start");
        activateScene(scene); 
    } else {
        deactivateScene(scene);
    }

}

function activateScene(scene) {

    setState('scene.0.GrowthPhase.'+scene, true);
}

function deactivateScene(scene) {

    setState('scene.0.GrowthPhase.'+scene, false);
}



on('0_userdata.0.variables.active_growthphase_scene', function(dp) {
    log("Change growth scene to: " + dp.state.val);
    deactivateAllScenes()
    prepareScene(dp.state.val);
});

